class WrappedPlayer {
    constructor(player) {
        if (!(player instanceof Player)) throw new TypeError("Parameter is not an instance of Player.")
        
        this.player = player;
        this.dimension = player.dimension;
    }
    
    getGameMode() {
        const matches = [];
        each(GameMode, mode => {
            if (this.testGameMode(mode)) matches.push(mode);
        });
        return matches[0]
    }
    testGameMode(mode) {
        const playersUnderMode = this.dimension.getPlayers({ gameMode: mode });
        return playersUnderMode.some((player => player.id === this.player.id))
    }
    setGameMode(mode) {
        if (!Object.values(GameMode).includes(mode)) throw new TypeError("Unknown gamemode.")
        Commands.run(`gamemode ${mode}`, this.player);
    }
}

const ALL_DATABASES = new Map();

class DB extends Database {
    constructor(player) {
        super(player, "scoreboard-statistic");
    }
    
    events = new Map()
    
    // 此次因为有 events 这个运行时存储项，
    // 而不能多次 construct，
    // 故使用此方法将实例储存到 ALL_DATABASES 中
    static init(player) {
        const db = new this(player);
        ALL_DATABASES.set(player, db);
        return db
    }
    
    async set(objectiveId, criteria, events) {
        await super.set(objectiveId, criteria);
        this.events.set(objectiveId, events);
    }
    async clear() {
        await super.clear();
        this.events.clear();
    }
    async delete(objectiveId) {
        await super.delete(objectiveId);
        this.events.delete(objectiveId);
    }
    get(objectiveId) {
        const criteria = super.get(objectiveId);
        const events = this.events.get(objectiveId);
        return {
            criteria,
            events
        }
    }
    getAll() {
        const output = {};
        each(this, ([ objectiveId, criteria ]) => {
            const events = this.events.get(objectiveId);
            output[objectiveId] = { criteria, events };
        });
        return output
    }
}

