function parseCriteria(criteria) {
    return criteria.split(":").map((e)=>e.replace(".", ":")).map((e, i)=>{
        if (i === 0) return e;
        return e.match(/^(.+)\:/) ? e : `minecraft:${e}`;
    });
}

